import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import Layout from './Layout';
import axios from 'axios';

const CategoryPage = ({ isLoggedIn, setIsLoggedIn }) => {
    const { category } = useParams();
    const [subcategories, setSubcategories] = useState([]);

    useEffect(() => {
        // Alt kategorileri API'den çek
        const fetchSubcategories = async () => {
            try {
                const response = await axios.get(`http://localhost:5000/api/subcategories/${category}`);
                setSubcategories(response.data.map((item) => item.subcategory_name));
            } catch (error) {
                console.error('Error fetching subcategories:', error);
            }
        };
        fetchSubcategories();
    }, [category]);

    return (
        <Layout
            isLoggedIn={isLoggedIn}
            setIsLoggedIn={setIsLoggedIn}
            categories={[]} // Ana kategoriler boş bırakılır
            subcategories={subcategories} // Alt kategoriler Layout'a gönderilir
        >
            <h1>{category} Sayfası</h1>
            <p>Bu sayfada {category} kategorisine ait ürünler listelenecek.</p>
        </Layout>
    );
};

export default CategoryPage;
