import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';
import './styles/Products.css';
import Reviews from './Reviews';

const ProductPage = ({ isLoggedIn, setIsLoggedIn, userId }) => {
    const { productId } = useParams(); 
    const navigate = useNavigate();
    const [product, setProduct] = useState(null);  
    const [sellerInfo, setSellerInfo] = useState([]);  
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const [isFavorite, setIsFavorite] = useState(false);
    const [viewCount, setViewCount] = useState(0);
    const [attributes, setAttributes] = useState([]);


    // √úr√ºn bilgileri
    useEffect(() => {
        const fetchProductDetails = async () => {
            try {
                const response = await axios.get(`http://localhost:5000/api/products/${productId}`);
                console.log('API Response:', response.data); // Log 

                if (response.data && response.data.length > 0) {
                    const productData = response.data[0];  
                    setProduct(productData);
                    console.log('Product Data:', productData); // Log 
                } else {
                    setError("√úr√ºn bilgileri bulunamadƒ±.");
                    console.error("√úr√ºn bilgileri bulunamadƒ±.");
                }
                setLoading(false);
            } catch (err) {
                setError('√úr√ºn bilgisi alƒ±namadƒ±. L√ºtfen tekrar deneyin.');
                setLoading(false);
                console.error('Error fetching product details:', err);
            }
        };

        fetchProductDetails();
    }, [productId, isLoggedIn]);  

    // Fiyatlar ve satƒ±cƒ± ratingleri
    useEffect(() => {
        const fetchSellerInfo = async () => {
            try {
                const response = await axios.get(`http://localhost:5000/api/products/${productId}/prices`);
                console.log('API Response:', response.data); // Log 

                if (response.data) {
                    setSellerInfo(response.data);
                    console.log('Seller Info:', response.data); //Log
                } else {
                    setError("Fiyatlar veya satƒ±cƒ± ratingleri bulunamadƒ±.");
                    console.error("Fiyatlar veya satƒ±cƒ± ratingleri bulunamadƒ±.");
                }
                setLoading(false);
            } catch (err) {
                setError('Fiyatlar veya satƒ±cƒ± ratingleri alƒ±namadƒ±. L√ºtfen tekrar deneyin.');
                setLoading(false);
                console.error('Error fetching seller info:', err);
            }
        };

        fetchSellerInfo();
    }, [productId, isLoggedIn]);  

    useEffect(() => {
        const fetchAttributes = async () => {
            try {
                const response = await axios.get(`http://localhost:5000/api/products/${productId}/attributes`);
                console.log('Attributes API Response:', response.data); // Log the response
    
                if (response.data) {
                    setAttributes(response.data);
                } else {
                    setError("Attribute deƒüerleri bulunamadƒ±.");
                    console.error("Attribute deƒüerleri bulunamadƒ±.");
                }
                setLoading(false);
            } catch (error) {
                console.error('Error fetching attributes:', error);
                setError('Attribute deƒüerleri alƒ±namadƒ±. L√ºtfen tekrar deneyin.');
                setLoading(false);
            }
        };
    
        fetchAttributes();
    }, [productId]);


    // Favori durumu
    useEffect(() => { 
        const checkFavoriteStatus = async () => {
            if (!userId) return; // Kullanƒ±cƒ± ID'si yoksa favori durumu kontrol edilmez

            try {
                const response = await fetch(`http://localhost:5000/api/favorites/${userId}`);
                const favorites = await response.json();
                setIsFavorite(favorites.some((fav) => fav.product_id === product?.product_id));
            } catch (error) {
                console.error('Favori durumu kontrol edilemedi:', error);
            }
        };
    
        checkFavoriteStatus();
    }, [userId, product?.product_id]);


    useEffect(() => {
        // √úr√ºn sayfasƒ± a√ßƒ±ldƒ±ƒüƒ±nda tetikleyebilirsiniz
        if (product?.product_id) {
            handleViewProduct(product.product_id);
        }
    }, [product?.product_id]);

    const handleViewProduct = async (productId) => {
        try {
            if (isLoggedIn) {
                const response = await fetch('http://localhost:5000/api/view-product', {
                    method: 'POST',
                    headers: { 'Content-Type': 'application/json' },
                    body: JSON.stringify({ product_id: productId, user_id: userId })
                });
                if (!response.ok) {
                    console.error('√úr√ºn g√∂r√ºnt√ºleme kaydƒ± ba≈üarƒ±sƒ±z.', await response.text());
                    return;
                }
            }

            // G√∂r√ºnt√ºlenme sayƒ±sƒ±nƒ± almak i√ßin
            const visitsResponse = await fetch(`http://localhost:5000/api/view-count/${productId}`);
            if (visitsResponse.ok) {
                const { visit_count } = await visitsResponse.json();
                setViewCount(visit_count || 0);
            }
        } catch (error) {
            console.error('√úr√ºn g√∂r√ºnt√ºleme isteƒüi atƒ±lamadƒ±:', error);
        }
    };

    const handleFavoriteClick = async () => {
        if (!isLoggedIn) {
            alert('L√ºtfen favorilere eklemek i√ßin giri≈ü yapƒ±n.');
            return;
        }

        try {
            const response = await fetch('http://localhost:5000/api/toggle-favorite', {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify({ user_id: userId, product_id: product.product_id }),
            });

            if (response.ok) {
                setIsFavorite((prev) => !prev);
                alert(isFavorite ? 'Favorilerden √ßƒ±karƒ±ldƒ±.' : 'Favorilere eklendi.');
            } else {
                const error = await response.json();
                alert(`Hata: ${error.message}`);
            }
        } catch (error) {
            console.error('Favori durumu deƒüi≈ütirilemedi:', error);
            alert('Favori durumu deƒüi≈ütirilemedi.');
        }
    };

    if (loading) return <p>Y√ºkleniyor...</p>;
    if (error) return <p style={{ color: 'red' }}>{error}</p>;

    return (
        <div className="product-page">
            <button className="backbutton" onClick={() => navigate(-1)}>
                &#11164; Geri
            </button>

            {/* √úr√ºn bilgilerini g√∂ster */}
            {product ? (
                <div key={product.product_id}>
                    <img
                        src={product.img_url}
                        alt={product.product_name}
                        style={{ width: '200px', height: '200px', objectFit: 'cover' }}
                    />

                    <div style={{ display: 'flex', alignItems: 'center' }}>
                        <h2 style={{ marginRight: '8px' }}>{product.product_name}</h2>
                        <button
                            className="favorite-button"
                            onClick={handleFavoriteClick}
                            style={{
                                background: 'none',
                                border: 'none',
                                cursor: 'pointer',
                                fontSize: '24px',
                                color: isFavorite ? 'red' : 'gray',
                            }}
                            aria-label={isFavorite ? 'Favorilerden √ßƒ±kar' : 'Favorilere ekle'}
                        >
                            {isFavorite ? '‚ù§' : 'ü§ç'}
                        </button>
                    </div>
                    <p><strong>G√∂r√ºnt√ºlenme:</strong> {viewCount}</p>
                    <p><strong>A√ßƒ±klama:</strong> {product.product_description || 'A√ßƒ±klama bulunamadƒ±.'}</p>

                    <div style={{ flex: 1.5 }}>
                    {sellerInfo.map((seller, index) => (
                        <div key={index} style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '8px' }}>
                            <p style={{ flex: 0.3, textAlign: 'left' }}><strong>Satƒ±cƒ±:</strong> {seller.seller_name || 'Satƒ±cƒ± bilgisi yok'}</p>
                            <p style={{ flex: 0.3, textAlign: 'center' }}>Fiyat: {seller.price} TL</p>
                            <p style={{ flex: 0.3, textAlign: 'right' }}>Ortalama Puan: {seller.seller_rating || 'Hen√ºz deƒüerlendirme yok'}</p>
                        </div>
                    ))}
                </div>
                </div>
            ) : (
                <p>√úr√ºn bilgileri bulunamadƒ±.</p>
            )}
             <div style={{ marginTop: '20px' }}>
                <h3>√úr√ºn √ñzellikleri</h3>
                {attributes.map((attribute, index) => (
                    <p key={index}>{attribute.attribute_value}</p>
                ))}
            </div>


            <Reviews productId={productId} isLoggedIn={isLoggedIn} userId={userId} />
        </div>
    );
};

export default ProductPage;
