import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import axios from 'axios';
import Homepage from './Homepage';
import CategoryPage from './CategoryPage';
import LoginPage from './LoginPage';

const App = () => {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [categories, setCategories] = useState([]); // Kategoriler state'i

  useEffect(() => {
    // Kategorileri API'den çek
    const fetchCategories = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/categories');
        setCategories(response.data.map((item) => item.categories)); // Kategorileri state'e ekle
      } catch (error) {
        console.error('Error fetching categories:', error);
      }
    };
    fetchCategories();
  }, []);

  return (
    <Router>
      <Routes>
        {/* Ana Sayfa */}
        <Route
          path="/"
          element={
            <Homepage
              isLoggedIn={isLoggedIn}
              setIsLoggedIn={setIsLoggedIn}
              categories={categories}
            />
          }
        />

        {/* Login Sayfası */}
        <Route
          path="/login"
          element={
            <LoginPage setIsLoggedIn={setIsLoggedIn} />
          }
        />

        {/* Dinamik Kategori Sayfaları */}
        <Route
          path="/:category"
          element={<CategoryPage category={location.pathname.split('/')[1]} />}
        />
      </Routes>
    </Router>
  );
};

export default App;
