import React, { useState, useEffect } from 'react';
import './styles/Sidebar.css';
import { useNavigate, useLocation } from 'react-router-dom';

const Sidebar = ({ content, backButtonPath }) => {
    const [isMenuOpen, setIsMenuOpen] = useState(() => {
        const savedState = sessionStorage.getItem('isMenuOpen');
        return savedState === 'true';
    });
    const navigate = useNavigate();
    const location = useLocation();

    const toggleMenu = () => {
        const newState = !isMenuOpen;
        setIsMenuOpen(newState);
        sessionStorage.setItem('isMenuOpen', newState);
    };

    const handleBack = () => {
        if (backButtonPath) {
            navigate(backButtonPath);
        } else {
            navigate('/');
        }
    };

    useEffect(() => {
        if (location.pathname.startsWith('/products/')) {
            setIsMenuOpen(false);
            sessionStorage.setItem('isMenuOpen', 'false');
        }
    }, [location.pathname]);

    return (
        <>
            <div className="hamburger-icon" onClick={toggleMenu}>
                ☰
            </div>
            <div className={`sidebar ${isMenuOpen ? 'open' : ''}`}>
                <div className="back-button" onClick={handleBack}>⮜ geri dön</div>
                {content}
            </div>
        </>
    );
};

export default Sidebar;
