import React, { useEffect, useState } from 'react';

const FavoritesPage = ({ userId }) => {
    const [favorites, setFavorites] = useState([]);

    useEffect(() => {
        const fetchFavorites = async () => {
            if (!userId) {
                console.error('User ID is not provided.');
                return;
            }

            try {
                const response = await fetch(`http://localhost:5000/api/favorites/${userId}`);
                const data = await response.json();

                // API yanıtını kontrol edin ve bir dizi olup olmadığını doğrulayın
                if (Array.isArray(data)) {
                    setFavorites(data);
                } else {
                    console.error('API yanıtı beklenen formatta değil:', data);
                }
            } catch (error) {
                console.error('Error fetching favorites:', error);
            }
        };

        fetchFavorites();
    }, [userId]);

    return (
        <div>
            <h1>Favori Ürünler</h1>
            <div className="product-list">
                {favorites.map((favorite) => (
                    <div className="product-card" key={favorite.product_id}>
                        <img src={favorite.img_url} alt={favorite.product_name} />
                        <h2>{favorite.product_name}</h2>
                        <p>Fiyat: {favorite.lowest_price} TL</p>
                        <p>Ortalama Puan: {favorite.average_rating || 'Henüz değerlendirme yok'}</p>
                    </div>
                ))}
            </div>
        </div>
    );
};

export default FavoritesPage;
