import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './Layout.css';

const Layout = ({ children, isLoggedIn, setIsLoggedIn, categories, subcategories }) => {
    const [isLeftMenuOpen, setIsLeftMenuOpen] = useState(false);
    const [isRightMenuOpen, setIsRightMenuOpen] = useState(false);
    const [searchTerm, setSearchTerm] = useState('');
    const navigate = useNavigate();

    const toggleLeftMenu = () => setIsLeftMenuOpen(!isLeftMenuOpen);
    const toggleRightMenu = () => setIsRightMenuOpen(!isRightMenuOpen);

    const handleLogout = () => {
        setIsLoggedIn(false);
        navigate('/');
    };

    const goBack = () => navigate('/'); // Geri dön fonksiyonu

    const handleSearch = (event) => {
        setSearchTerm(event.target.value);
        if (event.key === 'Enter') {
            navigate(`/search?query=${searchTerm}`);
        }
    };

    return (
        <div className="layout">
            <h1 className='akakceHeader'>Ak Akçe Kara Gün İçindir</h1>
            {/* Sol Hamburger Menü */}
            <div className="hamburger-icon left" onClick={toggleLeftMenu}>
                ☰
            </div>
            <div className={`hamburger-menu left-menu ${isLeftMenuOpen ? 'open' : ''}`}>
                {subcategories && subcategories.length > 0 ? (
                    <>
                        {/* Alt Kategoriler Menüsü */}
                        <div className="back-button" onClick={goBack}>
                            ← Ana Sayfaya Dön
                        </div>
                        <h3>Alt Kategoriler</h3>
                        <ul>
                            {subcategories.map((subcategory, index) => (
                                <li key={index}>{subcategory}</li>
                            ))}
                        </ul>
                    </>
                ) : (
                    <>
                        {/* Ana Kategoriler Menüsü */}
                        <h3>Kategoriler</h3>
                        <ul>
                            {categories.map((category, index) => (
                                <li key={index} onClick={() => navigate(`/${category}`)}>
                                    {category}
                                </li>
                            ))}
                        </ul>
                    </>
                )}
            </div>

            {/* Sağ Hamburger Menü */}
            <div className="hamburger-icon right" onClick={toggleRightMenu}>
                ☰
            </div>
            <div className={`hamburger - menu right-menu ${isRightMenuOpen ? 'open' : ''}`}>
                <h3>Kullanıcı İşlemleri</h3>
                <ul>
                    {isLoggedIn ? (
                        <>
                            <li>Favoriler</li>
                            <li>Arama Geçmişi</li>
                            <li>Hesap Ayarları</li>
                            <li onClick={handleLogout}>Çıkış Yap</li>
                        </>
                    ) : (
                        <li onClick={() => navigate('/login')}>Giriş Yap</li>
                    )}
                </ul>
            </div>

            {/* Arama çubuğu */}
            <div className="search-bar">
                <input
                    type="text"
                    placeholder="Ürün ara..."
                    className="search-input"
                    value={searchTerm}
                    onChange={(e) => setSearchTerm(e.target.value)}
                    onKeyDown={handleSearch}
                />
            </div>

            {/* Ana İçerik */}
            <div className="content">{children}</div>
        </div >
    );
};

export default Layout;
