import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import Layout from './Layout';

const FavoritesPage = ({ userId, categories }) => {
    const [favorites, setFavorites] = useState([]);
    const navigate = useNavigate();

    useEffect(() => {
        const fetchFavorites = async () => {
            if (!userId) {
                console.error('User ID is not provided.');
                return;
            }

            try {
                const response = await fetch(`http://localhost:5000/api/favorites/${userId}`);
                const data = await response.json();

                // API yanıtını kontrol edin ve bir dizi olup olmadığını doğrulayın
                if (Array.isArray(data)) {
                    setFavorites(data);
                } else {
                    console.error('API yanıtı beklenen formatta değil:', data);
                }
            } catch (error) {
                console.error('Error fetching favorites:', error);
            }
        };

        fetchFavorites();
    }, [userId]);

    // Kategoriye tıklama işlemi
    const handleCategoryClick = (category) => {
        navigate(`/category/${category}`);
    };

    // Ürüne tıklama işlemi
    const handleProductClick = (productId) => {
        navigate(`/products/${productId}`);
    };

    const sidebarContent = (
        <>
            <h3>Kategoriler</h3>
            <ul>
                {categories.map((category, index) => (
                    <li
                        key={index}
                        onClick={() => handleCategoryClick(category)}
                        style={{ cursor: 'pointer' }}
                    >
                        {category}
                    </li>
                ))}
            </ul>
        </>
    );

    return (
        <Layout sidebarContent={sidebarContent}>
            <div>
                <h1>Favori Ürünler</h1>
                <div className="product-list">
                    {favorites.map((favorite) => (
                        <div
                            className="product-card"
                            key={favorite.product_id}
                            onClick={() => handleProductClick(favorite.product_id)}
                            style={{ cursor: 'pointer' }}
                        >
                            <img src={favorite.img_url} alt={favorite.product_name} />
                            <h2>{favorite.product_name}</h2>
                            <p>Fiyat: {favorite.lowest_price} TL</p>
                            <p>Ortalama Puan: {favorite.average_rating || 'Henüz değerlendirme yok'}</p>
                        </div>
                    ))}
                </div>
            </div>
        </Layout>
    );
};

export default FavoritesPage;
