import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import axios from 'axios';
import Homepage from './Homepage';
import CategoryPage from './CategoryPage';
import LoginPage from './LoginPage';
import Layout from './Layout';
import SignupPage from './SignupPage';
import AccountPage from './AccountPage';
import FavoritesPage from './FavoritesPage';
import Header from './Header';

const App = () => {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [categories, setCategories] = useState([]); // Kategoriler state'i

  useEffect(() => {
    const fetchCategories = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/categories');
        setCategories(response.data.map((item) => item.category_name));
      } catch (error) {
        console.error('Error fetching categories:', error);
      }
    };
    fetchCategories();
  }, []);

  return (
    <Router>
      <Header isLoggedIn={isLoggedIn} setIsLoggedIn={setIsLoggedIn} /> {/* Pass props */}
      <Routes>
        {/* Ana Sayfa */}
        <Route
          path="/"
          element={
            <Homepage
              isLoggedIn={isLoggedIn}
              setIsLoggedIn={setIsLoggedIn}
              categories={categories}
            />
          }
        />

        {/* Login Sayfası */}
        <Route
          path="/login"
          element={<LoginPage setIsLoggedIn={setIsLoggedIn} />}
        />

        <Route
          path="/signup"
          element={<SignupPage setIsLoggedIn={setIsLoggedIn} />}
        />

        {/* Account Page */}
        <Route
          path="/account"
          element={<AccountPage />}
        />

        {/* Favorites Page */}
        <Route
          path="/favorites"
          element={<FavoritesPage />}
        />

        {/* Dinamik Kategori Sayfaları */}
        <Route
          path="/:category"
          element={<CategoryPage />}
        />
      </Routes>
    </Router>
  );
};

export default App;
