import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './styles/Reviews.css';

const Reviews = ({ productId }) => {
    const [reviews, setReviews] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const [reviewText, setReviewText] = useState('');
    const [rating, setRating] = useState('');

    useEffect(() => {
        const fetchReviews = async () => {
            try {
                const response = await axios.get(`http://localhost:5000/api/get-reviews/${productId}`);
                setReviews(response.data);
                setLoading(false);
            } catch (err) {
                setError('Yorumlar alınamadı. Lütfen tekrar deneyin.');
                setLoading(false);
            }
        };

        fetchReviews();
    }, [productId]);

    const handleSubmitReview = async (e) => {
        e.preventDefault();
        try {
            await axios.post('http://localhost:5000/api/post-reviews', {
                user_id: userId,
                product_id: productId,
                review_text: reviewText,
                rating: parseInt(rating, 10)
            });
            setReviewText('');
            setRating('');
            // Re-fetch reviews to show the newly added one
            const response = await axios.get(`http://localhost:5000/api/get-reviews/${productId}`);
            setReviews(response.data);
        } catch (err) {
            setError('Yorum eklenirken bir hata oluştu. Lütfen tekrar deneyin.');
        }
    };

    if (loading) return <p>Yükleniyor...</p>;
    if (error) return <p style={{ color: 'red' }}>{error}</p>;

    return (
        <div className="reviews">

            <h3>Yorumlar</h3>
            {userId ? (
                <form onSubmit={handleSubmitReview} style={{ marginTop: '20px' }}>
                    <div>
                        <label htmlFor="reviewText">Yorumunuz:</label><br />
                        <textarea
                            id="reviewText"
                            value={reviewText}
                            onChange={(e) => setReviewText(e.target.value)}
                            required
                        />
                    </div>
                    <div style={{ marginTop: '10px' }}>
                        <label htmlFor="rating">Puan:</label><br />
                        <input
                            id="rating"
                            type="number"
                            value={rating}
                            onChange={(e) => setRating(e.target.value)}
                            min="1"
                            max="5"
                            required
                        />
                    </div>
                    <button type="submit" style={{ marginTop: '10px' }}>Yorumu Gönder</button>
                </form>
            ) : (
                <p>Yorum eklemek için lütfen giriş yapın.</p>
            )}
            {reviews.length === 0 ? (
                <p>Henüz yorum yok.</p>
            ) : (
                reviews.map((review) => (
                    <div key={review.review_id} className="review">
                        <p><strong>{review.user_name}</strong></p>
                        <p>{review.review_text}</p>
                        <p>Puan: {review.rating}</p>
                        <p>Tarih: {new Date(review.review_date).toLocaleDateString()}</p>
                    </div>
                ))
            )}
        </div>
    );
};

export default Reviews;
