import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './Layout.css';

const Homepage = ({ isLoggedIn, setIsLoggedIn }) => {
    const [isLeftMenuOpen, setIsLeftMenuOpen] = useState(false); // Sol Menü (Kategoriler)
    const [isRightMenuOpen, setIsRightMenuOpen] = useState(false); // Sağ Menü (Kullanıcı İşlemleri)

    const navigate = useNavigate();

    // Menüleri açma/kapama fonksiyonları
    const toggleLeftMenu = () => setIsLeftMenuOpen(!isLeftMenuOpen);
    const toggleRightMenu = () => setIsRightMenuOpen(!isRightMenuOpen);

    const handleLogout = () => {
        setIsLoggedIn(false); // Oturumu kapat
        navigate('/'); // Ana sayfaya yönlendir
    };

    return (
        <div className="homepage">
            {/* Arama Çubuğu */}
            <div className="search-bar">
                <input
                    type="text"
                    placeholder="Ürün ara..."
                    className="search-input"
                />
            </div>

            {/* Sol Hamburger Menü - Kategoriler */}
            <div className="hamburger-icon left" onClick={toggleLeftMenu}>
                ☰
            </div>
            <div className={`hamburger-menu left-menu ${isLeftMenuOpen ? 'open' : ''}`}>
                <h3>Kategoriler</h3>
                <ul>
                    <li>Elektronik</li>
                    <li>Ev ve Ofis</li>
                    <li>Giyim</li>
                    <li>Kozmetik</li>
                    <li>Spor</li>
                </ul>
            </div>

            {/* Sağ Hamburger Menü - Kullanıcı İşlemleri */}
            <div className="hamburger-icon right" onClick={toggleRightMenu}>
                ☰
            </div>
            <div className={`hamburger-menu right-menu ${isRightMenuOpen ? 'open' : ''}`}>
                <h3>Kullanıcı İşlemleri</h3>
                <ul>
                    {isLoggedIn ? (
                        <>
                            <li>Favoriler</li>
                            <li>Arama Geçmişi</li>
                            <li>Hesap Ayarları</li>
                            <li onClick={handleLogout}>Çıkış Yap</li>
                        </>
                    ) : (
                        <li onClick={() => navigate('/login')}>Giriş Yap</li>
                    )}
                </ul>
            </div>

            {/* Ana Sayfa İçeriği */}
            <h1>Hoş Geldiniz!</h1>
        </div>
    );
};

export default Homepage;
