import React, { useState, useEffect } from 'react';
import { useLocation } from 'react-router-dom'; // useLocation ekleniyor
import Layout from './Layout.js';
import axios from 'axios';

const CategoryPage = () => {
    const location = useLocation(); // URL bilgisini alıyoruz
    const category = location.pathname.split('/')[1]; // URL'den kategoriyi çıkarıyoruz

    const [subcategories, setSubcategories] = useState([]);
    const [expandedSubcategory, setExpandedSubcategory] = useState(null);

    useEffect(() => {
        const fetchSubcategories = async () => {
            try {
                const response = await axios.get(`/api/subcategories/${category}`);
                setSubcategories(response.data); // Gelen veriyi doğrudan kullanıyoruz
            } catch (error) {
                console.error('Alt kategoriler alınamadı:', error);
            }
        };
        fetchSubcategories();
    }, [category]);

    const sidebarContent = (
        <>
            <h3>{category} Alt Kategorileri</h3>
            <ul>
                {subcategories.map((subcategory, index) => (
                    <li
                        key={index}
                        onClick={() => setExpandedSubcategory(subcategory.subcategory_id)}
                    >
                        {subcategory.subcategory_name}
                    </li>
                ))}
            </ul>
            {expandedSubcategory && <p>Öznitelikler burada olacak...</p>}
        </>
    );

    return (
        <Layout sidebarContent={sidebarContent}>
            <h1>{category} Sayfası</h1>
            <p>Bu sayfada ürünler listelenecek.</p>
        </Layout>
    );
};

export default CategoryPage;
